import { useEffect, useState } from "react";
import PrayerGetComponent from "./PrayerGetComponent";

const PrayerGet = () => {
  const [requests, setRequests] = useState([]);
  const [randomRequest, setRandomRequest] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/requests')
      .then(res => res.json())
      .then(data => {
        setRequests(data);
        setRandomRequest(data[Math.floor(Math.random() * data.length)]);
      });
  }, []);

  return (
    <div className="home">
      <PrayerGetComponent randomRequest={randomRequest} />
    </div>
  );
};

export { PrayerGet, PrayerGetComponent };
