import React, { useState } from 'react';
import './pgetcomp.css';

const generatePrayerExample = async (requestText) => {
  const apiKey = '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';
  const prompt = `Generate a prayer example for the request: "${requestText}"`;

  const response = await fetch('https://api.openai.com/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${apiKey}`,
    },
    body: JSON.stringify({
      model: 'gpt-3.5-turbo', // Use the appropriate model name
      messages: [{ role: 'system', content: 'You are a prayer generator.' }, { role: 'user', content: prompt }],
    }),
  });

  const data = await response.json();
  const prayerExample = data.choices[0]?.message?.content;

  return prayerExample;
};



const PrayerGetComponent = ({ requests }) => {
  const [randomRequest, setRandomRequest] = useState(null);

  const displayRandomRequest = async () => {
    const randomIndex = Math.floor(Math.random() * requests.length);
    const randomRequest = requests[randomIndex];
  
    // Generate a prayer example for the random request body
    const prayerExample = await generatePrayerExample(randomRequest.body);
  
    setRandomRequest({ ...randomRequest, prayerExample });
  };

  const handleDelete=()=>{
    fetch('http://localhost:8000/requests/' + randomRequest.id,{
    method: 'DELETE'}).then(displayRandomRequest)
  };

  return (
    <div className="currentrequest">
      
      {randomRequest && (
  <div className="blog-preview" key={randomRequest.id}>
    <h2>{randomRequest.name} from {randomRequest.location}'s prayer request:</h2>
    <h4>"{randomRequest.body}"</h4>
    <p>Generated Prayer Example: {randomRequest.prayerExample}</p>
    <button onClick={handleDelete}>Complete and Remove Request</button>
  </div>
)}
      
      <button onClick={displayRandomRequest}>Display Random Prayer Request</button>
    </div>
  );
};

export default PrayerGetComponent;
