  useEffect(() => {
    fetch('http://localhost:8000/requests')
      .then(res => {
        return res.json();
      })
      .then(data => {
        setRequests(data);
        setRandomRequest(data[Math.floor(Math.random() * data.length)]);
      })
  }, [])

  return (
    <div className="home">
      {requests && <PrayerGetComponent randomRequest={randomRequest} />}
    </div>
  );
  };
  
