import { useEffect, useState } from "react";
import PrayerGetComponent from "./PrayerGetComponent";

const PrayerGet = () => {
  const [randomRequest, setRandomRequest] = useState(null);
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/requests')
      .then(res => res.json())
      .then(data => {
        setRequests(data.requests);
      });
  }, []);

  useEffect(() => {
    if (requests.length > 0) {
      const randomIndex = Math.floor(Math.random() * requests.length);
      setRandomRequest(requests[randomIndex]);
    }
  }, [requests]);

  return (
    <div className="home">
      {randomRequest && <PrayerGetComponent request={randomRequest} />}
    </div>
  );
};

export default PrayerGet;
